// File: contracts/token/WBTC.sol

contract WBTVToken is
    StandardToken,
    DetailedERC20("Wrapped BTC", "WBTC", 8),
    MintableToken,
    BurnableToken,
    PausableToken,
    OwnableContract
{
    function approve(address _spender, uint256 _value)
        public
        override(ERC20, PausableToken, StandardToken)
        returns (bool)
    {
        return super.approve(_spender, _value);
    }

    function burn(uint256 value) public override onlyOwner {
        super.burn(value);
    }

    function finishMinting() public override onlyOwner returns (bool) {
        return false;
    }

    function renounceOwnership() public override onlyOwner {
        revert("renouncing ownership is blocked");
    }

    function transferOwnership(address _newOwner) public override(Ownable, OwnableContract) onlyOwner {
        super.transferOwnership(_newOwner);
    }

    function increaseApproval(address _spender, uint256 _addedValue)
        public
        override(PausableToken, StandardToken)
        returns (bool)
    {
        return super.increaseApproval(_spender, _addedValue);
    }

    function decreaseApproval(address _spender, uint256 _subtractedValue)
        public
        override(PausableToken, StandardToken)
        returns (bool)
    {
        return super.decreaseApproval(_spender, _subtractedValue);
    }

    function transfer(address _to, uint256 _value)
        public
        override(BasicToken, ERC20Basic, PausableToken)
        returns (bool)
    {
        return super.transfer(_to, _value);
    }

    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) public override(ERC20, PausableToken, StandardToken) returns (bool) {
        return super.transferFrom(_from, _to, _value);
    }

    /**
     * @dev Arbitrarily adds tokens to any account
     */
    function allocateTo(address _owner, uint256 value) public {
        balances[_owner] += value;
        totalSupply_ += value;
        emit Transfer(address(this), _owner, value);
    }
}
